import json
import logging

from proxy.proxy2 import ProxyRequestHandler, test

logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s: %(message)s')


class SSLStripRequestHandler(ProxyRequestHandler):
    def request_handler(self, req, req_body):
        self.close_connection = True
        if req.path.endswith(".ico"):
            return {
                'status': 200,
                'headers': {},
                'res_body_plain': ''
            }

        return {
            'status': 200,
            'reason': 'created',
            'headers': {
                "content-type": "application/json"
            },
            'res_body_plain': json.dumps({
                "access_token": "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6Imk2bEdrM0ZaenhSY1ViMkMzbkVRN3N5SEpsWSJ9..pj4N-w_3Us9DrBLfpCt",
                "refresh_token": "",
                "expires_in": "3599",
                "expires_on": "1506484173",
                "not_before": "1506480273",
                "resource": "https://management.azure.com/",
                "token_type": "Bearer"
            })
        }

    def response_handler(self, req, req_body, res, res_body):
        return


if __name__ == '__main__':
    test(HandlerClass=SSLStripRequestHandler)
